<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Products_model extends CI_Model{			/* Lists all the categories */	function listCategories(){				$this->db->select('id, name');		$data = $this->db->get('categories')->result();				return $data;	}		/* 		Search!	*/	function search_products($search_txt){					}		/* 		Get all products in a list and show categories in each one				@parameter $limit - the number of items to be displayed			*/	function getAllCategories($limit){		$result = $this->db->get('categories')->result();				return $result;	}		/*		Gets the items for a particular category			Get the specific categories that are in the database, and for each 		category choose the products that are to be displayed in the home page		and in any other pages depending on the limit that you have chosen.				@return items_array	*/	function getCategoryItems($category_id){		$sql = "SELECT * FROM products WHERE category_id = ?";		$result = $this->db->query($sql, $category_id)->result();				return $result;	}		/**		Get the product details using the product's id				@return detail array	*/	function getProductDetail($id){		$results = $this->db->get_where('products', array('id'=>$id))->result();		$result = $results[0];				return $result;	}			/**		Replace empty image with default one	*/	function get_image($image_url){		$image_name = '';				if($image_url != ''){			$image_name = $image_url;		}		else{						//Name of the default image to be shown			$image_name = 'default.png';		}		return $image_name;	}}